import HomePage from "../page-objects/HomePage";
import * as checks from '../utils/check';

var {Then} = require('cucumber');
var {When} = require('cucumber');
var {Given} = require('cucumber');
const assert = require('assert');

const homePage = 'HomePage';


Given(/^I am on webdriver university homepage$/, function () {
    browser.url('/');
    const pageDetails = browser.getUrlAndTitle();
    expect(pageDetails.url).to.contain('webdriveruniversity');
    assert.strictEqual(pageDetails.title, 'WebDriverUniversity.com');
    // const title = browser.getTitle();
    // const pageTtile = browser.getTitle();
    // assert.strictEqual(title, "WebDriverUniversity.com");
    // expect(pageTtile).to.contains('WebDriverUniversity');
    // //console.log('title of the page is:' + title);
});
When(/^click on contact us page link$/, function () {
    HomePage.contactPageButton.click();
    //browser.switchWindow('WebDriver | Contact Us');
});


When(/^I click on the login portal link$/, function () {
    HomePage.loginPortalButton.click();

});
Then(/^I should be landed on login page$/, function () {
    browser.switchWindow('WebDriver | Login Portal');
    browser.pause(5000);
    const pageUrl = browser.getUrl();
    expect(pageUrl).to.contains('Login-Portal');
});

