import ContactUsPage from "../page-objects/ContactUsPage";
import * as checks from '../utils/check';

var {Then} = require('cucumber');
var {When} = require('cucumber');
var {Given} = require('cucumber');
const assert = require('assert');

Then(/^I should be directed to contact us page$/, function () {
    browser.switchWindow('WebDriver | Contact Us');
    const pageDetails = browser.getUrlAndTitle();
    expect(pageDetails.url).to.contain('Contact-Us');
    //const pageUrl = browser.getUrl();
    //expect(pageUrl).to.contains('Contact-Us');
    assert.strictEqual(pageDetails.title, "WebDriver | Contact Us")
    browser.pause(5000);

});

Then(/^I submit ([^"]*)?, ([^"]*)?, ([^"]*)?, ([^"]*)?$/, (firstName, lastName, email, comments) => {
ContactUsPage.submitContactUsForm(firstName, lastName, email, comments);
});

Then(/^I submit the form$/, function () {
    ContactUsPage.submitButton.click();
    browser.pause(5000);
});

Then(/^I should see the ([^"]*)?$/, message => {
    const headerMessage = ContactUsPage.successMessageHeader.getText();
    assert.strictEqual(headerMessage, message);
});

