import BasePage from './BasePage';

const path = '/Contact-Us/contactus.html/';


class ContactUsPage extends BasePage {

    constructor() {
        super();
        super.path = path;
        this.title = 'ContactUsPage';
    }

    get firstNameInputField() {
      //  return $("form#contact_form > input[name='first_name']");
        return $("[name='first_name']");

    }

    get lastNameInputField() {
        //return $("form#contact_form > input[name='last_name']");
        return $("[name='last_name']");
    }

    get emailInputField() {
        //return $("form#contact_form > input[name='email']");
        return $("[name='email']");

    }

    get commentsInputField() {
        //return $("form#contact_form > textarea[name='message']")
        return $("[name='message']");
    }

    get submitButton() {
       // return $('div#form_buttons > input:nth-of-type(2)');
        return $('#form_buttons').$('[value="SUBMIT"]');
    }

    get successMessageHeader() {
        return $('#contact_reply');
    }

    submitContactUsForm(firstName, lastName, email, comments) {
        this.firstNameInputField.setValue(firstName);
        this.lastNameInputField.setValue(lastName);
        this.emailInputField.setValue(email);
        this.commentsInputField.setValue(comments);
    }
}

export default new ContactUsPage();

